import React from 'react';
import { useForm } from 'react-hook-form';
import List from './List';
import { useAddTransactionMutation } from '../redux/apiSlice';

export default function Form() {
  const { register, handleSubmit, reset } = useForm();
  const [addTransaction] = useAddTransactionMutation();

  const onSubmit = async (data) => {
    if (!data) return;

    try {
      // Assuming your API call for adding a transaction works with the `addTransaction` function.
      await addTransaction(data).unwrap();
      reset();
    } catch (error) {
      console.error('Error adding transaction:', error);
    }
  };

  return (
    <div className="form">
      <h2>Add Transaction</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="input-group">
          <input type="text" {...register('name')} placeholder="Name" className="form-input" />
        </div>
        <div className="input-group">
          <select {...register('type')} className="form-input">
            <option value="Investment">Investment</option>
            <option value="Expense">Expense</option>
            <option value="Savings">Savings</option>
          </select>
        </div>
        <div className="input-group">
          <input type="number" {...register('amount')} placeholder="Amount" className="form-input" />
        </div>
        <div className="submit-btn">
          <button type="submit" className="border py-2 text-white bg-indigo-500 w-full">Make Transaction</button>
        </div>
      </form>
      <List />
    </div>
  );
}
