import React from 'react'
import { Chart, ArcElement } from 'chart.js'
import { Doughnut } from 'react-chartjs-2'
import Label from './Label'
import { useSelector } from 'react-redux';

Chart.register(ArcElement)

const Graph = () => {
  const transactionsFromStore = useSelector(state => state.expensesReducer.transactions);

  // Calculate the total amount from the remaining transactions
  const totalAmount = transactionsFromStore.reduce((total, transaction) => total + transaction.amount, 0);

  const chartData = {
    datasets: [
      {
        data: [/* Update this array with the amounts from your transactions */],
        backgroundColor: [
          'rgb(255, 99, 132)',
          'rgb(54, 162, 235)',
          'rgb(255, 205, 86)',
        ],
        hoverOffset: 4,
      },
    ],
  };


  return (
    <div className='flex justify-center max-w-xs mx-auto'>
      <div className='item'>
        <div className='chart relative'>
          <Doughnut {...config} data={chartData} />
          <h3 className='mb-4 font-bold title'>
            Total
            <span className='block text-3xl text-emerald-400'>&#x20b9;{totalAmount}</span>
          </h3>
        </div>
        <div className='flex flex-col py-10 gap-4'>
          <Label />
        </div>
      </div>
    </div>
  );
};

export default Graph
