import React from 'react';
import { useDispatch } from 'react-redux';
import { deleteTransaction } from '../redux/reduxer';
import { useSelector } from 'react-redux';

const TransactionsHistoryJSX = ({ transactions, onDelete }) => (
  <div className="flex flex-col py-6 gap-3">
    <h1 className="py-4 font-bold text-xl">History</h1>
    {transactions.map((transaction) => (
      <div
        key={transaction._id}
        className={`item flex justify-between py-2 rounded-r border-8 ${transaction.colorClass}`} // Apply border color class
      >
        <div className="flex flex-col w-full">
          <span className="block">{transaction.name}</span>
          <span className="block text-gray-600">Amount: {transaction.amount}</span>
        </div>
        <button
          onClick={() => onDelete(transaction._id)}
          className="px-3 bg-slate-600 rounded-lg text-white"
        >
          Delete
        </button>
      </div>
    ))}
  </div>
);

const List = () => {
  const dispatch = useDispatch();
  const transactionsFromStore = useSelector((state) => state.expensesReducer.transactions);

  const handleDelete = (id) => {
    dispatch(deleteTransaction(id));
  };

  // Create a mapping of colors to tailwind classes
  const colorClassMap = {
    'rgb(255, 99, 132)': 'border-red-500',
    'rgb(54, 162, 235)': 'border-blue-500',
    'rgb(255, 205, 86)': 'border-yellow-500',
  };

  // Attach the appropriate color class to each transaction
  const transactionsWithColorClass = transactionsFromStore.map((transaction) => ({
    ...transaction,
    colorClass: colorClassMap[transaction.color],
  }));

  return <TransactionsHistoryJSX transactions={transactionsWithColorClass} onDelete={handleDelete} />;
};

export default List;
