import React from 'react';
import { useDispatch } from 'react-redux';
import { deleteTransaction } from '../redux/reduxer';
import { useSelector } from 'react-redux';

const listCollection = [
  {
    name: 'Savings',
    color: 'rgb(255, 99, 132)',
    id: 1,
  },
  {
    name: 'Investments',
    color: 'rgb(54, 162, 235)',
    id: 2,
  },
  {
    name: 'Expenses',
    color: 'rgb(255, 205, 86)',
    id: 3,
  },
];

const TransactionsHistoryJSX = ({ transactions, onDelete }) => {
  const typeToColor = {};
  listCollection.forEach((item) => {
    typeToColor[item.name] = item.color;
  });

  return (
    <div className="flex flex-col py-6 gap-3">
      <h1 className="py-4 font-bold text-xl">History</h1>
      {transactions.map((transaction) => (
        <div
          key={transaction._id}
          className={`item flex justify-between bg-gray-100 py-2 rounded-r bg-${[transaction.name]}`} // Add color class
        >
          <div className="flex flex-col w-full ml-4">
            <span className="block text-white">{transaction.name}</span>
            <span className="block text-white">Amount: {transaction.amount}</span>
          </div>
          <button
            onClick={() => onDelete(transaction._id)}
            className="px-3 bg-red-500 rounded-lg text-white"
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

const List = () => {
  const dispatch = useDispatch();
  const transactionsFromStore = useSelector((state) => state.expensesReducer.transactions);

  const handleDelete = (id) => {
    dispatch(deleteTransaction(id));
  };

  return <TransactionsHistoryJSX transactions={transactionsFromStore} onDelete={handleDelete} />;
};

export default List;
