import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { default as api } from '../redux/apiSlice'
import { useQuery } from 'react-query';
import { addCategoriesToStore, addTransactionsToStore } from '../redux/reduxer'

// const calculateShareFromTransactionType

// TODO : calculate % later, currently set as 0 by default
const LableComponentJSX = ({ type, color = 'tomato', percent = '0%' }) => (
  <div className='labels flex justify-between'>
    <div className='flex gap-2'>
      <div
        className='w-2 h-2 rounded py-3'
        style={{ backgroundColor: `${color}` }}
      ></div>
      <h3 className='text-lg'>{type}</h3>
    </div>
    <h3 className='font-bold'>{percent}</h3>
  </div>
)

const Label = () => {
  const dispatch = useDispatch();
  const { categories: categoriesFromStore, transactions: transactionsFromStore } = useSelector(state => state.expensesReducer);

  const { data: categories, isLoading: categoriesLoading, isError: categoriesError } = useQuery('categories', api.useGetCategoriesQuery);
  const { data: transactions, isLoading: transactionsLoading, isError: transactionsError } = useQuery('transactions', api.useGetTransactionsQuery);

  useEffect(() => {
    if (!categoriesLoading && !categoriesError && categories) {
      dispatch(addCategoriesToStore(categories));
    }
  }, [categories, categoriesLoading, categoriesError, dispatch]);

  useEffect(() => {
    if (!transactionsLoading && !transactionsError && transactions) {
      dispatch(addTransactionsToStore(transactions));
    }
  }, [transactions, transactionsLoading, transactionsError, dispatch]);

  if (categoriesLoading || transactionsLoading) {
    return <p>Loading...</p>; // Optional loading indicator
  }

  if (categoriesError || transactionsError) {
    return <p>Error loading data</p>; // Optional error message
  }

  return (
    <>
      {transactionsFromStore.map(label => (
        <div key={Math.random()}>
          <LableComponentJSX type={label.type} />
          {/* Display categoriesFromStore here */}
          <div>{categoriesFromStore}</div>
        </div>
      ))}
    </>
  );

}

export default Label
