//import { createSlice } from '@reduxjs/toolkit'

// Action Types
const ADD_CATEGORIES_TO_STORE = 'ADD_CATEGORIES_TO_STORE';
const ADD_TRANSACTIONS_TO_STORE = 'ADD_TRANSACTIONS_TO_STORE';

// Action Creators
export const addCategoriesToStore = (categories) => ({
  type: ADD_CATEGORIES_TO_STORE,
  payload: categories,
});

export const addTransactionsToStore = (transactions) => ({
  type: ADD_TRANSACTIONS_TO_STORE,
  payload: transactions,
});

// Reducers
const initialState = {
  categories: [],
  transactions: [],
};

const expensesReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_CATEGORIES_TO_STORE:
      return {
        ...state,
        categories: action.payload,
      };
    case ADD_TRANSACTIONS_TO_STORE:
      return {
        ...state,
        transactions: action.payload,
      };
    default:
      return state;
  }
};

export default expensesReducer.reducer
