import React from 'react';
import { useDispatch } from 'react-redux';
import { deleteTransaction } from '../redux/reduxer';
import { useSelector } from 'react-redux';


const listCollection = [
  {
    name: 'Savings',
    color: 'rgb(255, 99, 132)',
    id: 1,
  },
  {
    name: 'Investments',
    color: 'rgb(54, 162, 235)',
    id: 2,
  },
  {
    name: 'Expenses',
    color: 'rgb(255, 205, 86)',
    id: 3,
  },
]

const TransactionsHistoryJSX = ({ transactions, onDelete }) => (
  <div className="flex flex-col py-6 gap-3">
    <h1 className="py-4 font-bold text-xl">History</h1>
    {listCollection.map(list => (
      <div
        key={list._id}
        className="item flex justify-between py-2 rounded-r"
        style={{ borderLeft: `8px solid ${list.color}` }}
      >
        <div className="flex flex-col w-full">
          <span className="block">{list.name}</span>
          <span className="block text-gray-600">Amount: {list.amount}</span>
        </div>
        <button
          onClick={() => onDelete(list._id)}
          className="px-3 bg-slate-600 rounded-lg text-white ml-2" // Added ml-2 to move the Delete button to the right
        >
          Delete
        </button>
      </div>
    ))}
  </div>
);

const List = () => {
  const dispatch = useDispatch();
  const transactionsFromStore = useSelector((state) => state.expensesReducer.transactions);

  const handleDelete = (id) => {
    dispatch(deleteTransaction(id));
  };

  return <TransactionsHistoryJSX transactions={transactionsFromStore} onDelete={handleDelete} />;
};

export default List;
