// components/List.js
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { deleteTransaction } from '../redux/reduxer';
import { deleteGraphData } from '../redux/graphDataSlice';
import axios from 'axios'; // Import Axios for making API requests

const listCollection = [
  {
    _id: '1',
    name: 'Savings',
    color: 'rgb(255, 99, 132)',
    id: 1,
    amount: 1000,
  },
  {
    _id: '2',
    name: 'Investments',
    color: 'rgb(54, 162, 235)',
    id: 2,
    amount: 2500,
  },
  {
    _id: '3',
    name: 'Expenses',
    color: 'rgb(255, 205, 86)',
    id: 3,
    amount: 500,
  },
];

const List = () => {
  const dispatch = useDispatch();
  const [items, setItems] = useState(listCollection);

  // Function to delete a transaction
  const handleDelete = async (id) => {
    // Remove the item from the state
    const updatedItems = items.filter((item) => item._id !== id);
    setItems(updatedItems);

    // Dispatch an action to delete the item from the Redux store (transaction history)
    dispatch(deleteTransaction(id));

    // Dispatch an action to delete the item from the graph data
    dispatch(deleteGraphData(id));

    // Make an API request to delete the transaction from the database
    try {
      await axios.delete(`/api/transaction/${id}`); // Replace with your API endpoint
      console.log(`Transaction with ID ${id} deleted from the database.`);
    } catch (error) {
      console.error('Error deleting transaction:', error);
    }
  };

  const TransactionsHistoryJSX = ({ id, name, color, onDelete, amount }) => (
    <>
      <div
        className="item flex justify-between bg-gray-50 py-2 rounded-r"
        style={{ borderRight: `8px solid ${color}` }}
      >
        <div className="flex flex-col w-full">
          <span className="block">{name}</span>
          <span className="block text-gray-600">Amount: {amount}</span>
        </div>
        <button
          onClick={() => handleDelete(id)} // Call the handleDelete function with the ID on button click
          className="px-3 bg-slate-600 rounded-lg text-white"
        >
          Delete
        </button>
      </div>
    </>
  );

  return (
    <>
      <div className="flex flex-col py-6 gap-3">
        <h1 className="py-4 font-bold text-xl">History</h1>
        {items.map((item) => (
          <TransactionsHistoryJSX
            key={item.id}
            id={item._id}
            name={item.name}
            color={item.color}
            onDelete={handleDelete}
            amount={item.amount}
          />
        ))}
      </div>
    </>
  );
};

export default List;
