import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useGetCategoriesQuery, useGetTransactionsQuery } from '../redux/apiSlice';
import { addCategoriesToStore, addTransactionsToStore } from '../redux/reduxer';

const Label = () => {
  const dispatch = useDispatch();
  const { categories: categoriesFromStore, transactions: transactionsFromStore } = useSelector((state) => state.expensesReducer);

  console.log({ categoriesFromStore, transactionsFromStore });

  const { data: categories } = useGetCategoriesQuery();
  const { data: transactions } = useGetTransactionsQuery();

  useEffect(() => {
    if (categories) {
      dispatch(addCategoriesToStore(categories));
    }
  }, [categories, dispatch]);

  useEffect(() => {
    if (transactions) {
      dispatch(addTransactionsToStore(transactions));
    }
  }, [transactions, dispatch]);

  return (
    <>
      {transactionsFromStore.map((label) => (
        <LabelComponentJSX
          key={Math.random()} // TODO: Apply proper key later
          type={label.type}
        />
      ))}
    </>
  );
};
export default Label;
