import React from 'react';
import { useDispatch } from 'react-redux';
import { deleteTransaction } from '../redux/reduxer'; // Import the action to delete a transaction
import { useSelector } from 'react-redux';

const TransactionsHistoryJSX = ({ transactions, onDelete }) => (
  <div className="flex flex-col py-6 gap-3">
    <h1 className="py-4 font-bold text-xl">History</h1>
    {transactions.map((transaction) => (
      <div
        key={transaction._id}
        className="item flex justify-between bg-gray-50 py-2 rounded-r"
        style={{ borderRight: `8px solid ${transaction.color}` }}
      >
        <span className="block w-full">{transaction.name}</span>
        <button
          onClick={() => onDelete(transaction._id)} // Call the onDelete function with the ID on button click
          className="px-3 bg-slate-600 rounded-lg text-white"
        >
          Delete
        </button>
      </div>
    ))}
  </div>
);

const List = () => {
  const dispatch = useDispatch();

  // Retrieve transactions from the Redux store
  const transactionsFromStore = useSelector((state) => state.expensesReducer.transactions);

  // Function to handle item deletion
  const handleDelete = (id) => {
    // Dispatch an action to delete the item from the Redux store (and potentially the database)
    dispatch(deleteTransaction(id));
  };

  return <TransactionsHistoryJSX transactions={transactionsFromStore} onDelete={handleDelete} />;
};

export default List;
